{##
  WARNING
  Be extra cautious when changing the contents of this file along with any CSS or JS that styles or manipulates this HTML.
  Due to the way Page Cache works, both logged-in and logged-out users may see pages with updated CSS and JS along with stale HTML.
  If needed, please split your PR into two or more parts to make all CSS / JS changes backwards-compatible.
  See https://jira.squarespace.net/browse/COE-1047 for details.
##}
{.if useFormsJs}
  <script type="application/json" id="form-context-{formId}" class="sqs-form-block-context">{formContext}</script>
  <div id="form-submission-html-{formId}" class="sqs-form-block-submission-html" data-submission-html="{formSubmissionHTML|htmlattr}"></div>
{.end}

{@|apply:block-background:
  :backgroundColorVariable=--tweak-form-block-background-color:
  :strokeColorVariable=--tweak-form-block-stroke-color:
}

{.if blockStatus}
  {@|apply block-status}
{.end}

{.if useLightbox}
  <div data-animation-role="button" class="sqs-site-style-form lightbox-handle-wrapper{.if buttonAlignment} lightbox-handle-wrapper--align-{buttonAlignment}{.end}">
    <button class="lightbox-handle sqs-system-button sqs-editable-button sqs-button-element--{.if buttonVariant}{buttonVariant}{.or}primary{.end}" data-sqsp-button>
      {.if lightboxHandleText}
        {lightboxHandleText|html}
      {.or}
        {localizedStrings.lightbox.openForm}
      {.end}
    </button>
  </div>
{.end}

<div class="sqs-site-style-form form-wrapper{.if useLightbox} hidden{.end}{.if blockStatus.placeholderContent} sqs-block-is-placeholder{.end}"
  {.if blockStatus.state}data-block-state="{blockStatus.state}"{.end}
>
  {.if showTitle}<div class="form-title">{formName|htmltag}</div>{.end}
  <div class="form-inner-wrapper" {.if useFormsJs} hidden{.end}>
    <form
       data-form-id="{formId}"
       data-success-redirect="{successRedirect}"
       data-dynamic-strings
       autocomplete="on"
       {.if preventSubmit}{.or}
       method="POST"
       action="{secureUrl}"
       novalidate
       onsubmit="return (function (form) {
  Y.use('squarespace-form-submit', 'node', function usingFormSubmit(Y) {
    (new Y.Squarespace.FormSubmit(form)).submit({
      formId: '{formId}',
      collectionId: '{collectionId}',
      objectName: '{objectName}'
    });
  });
  return false;
})(this);"
       {.end}
    >
      {.section formFields}
        <div class="field-list clear{.section type} form-type-{@}{.end}">

        {.repeated section @}

          {.macro individual-input-label}
            <label class="title" for="{id|htmlattr}-field">
              {.section title}{@}{.end}
              {.section required}
                <span class="required" aria-hidden="true">*</span>
              {.end}
            </label>
          {.end}

          {.macro fieldset-legend}
            <legend class="title">
              {.section title}{@}{.end}
              {.section required}
                <span class="required" aria-hidden="true">*</span>
              {.end}
            </legend>
          {.end}

            {.if hidden}
              <input type="hidden" class="form-item field hidden" id="{id|htmlattr}" name="{key|htmlattr}" value="{value|htmlattr}">
            {.end}

            {.if section}
              <div id="{id|htmlattr}" class="form-item section{.if underline} underline{.end}{.if required} required{.end}">
                <div class="title">{.section title}{@}{.end}</div>
                {.section description}<div class="description">{@}</div>{.end}
              </div>
            {.end}

            {.if text}
              <div id="{id|htmlattr}" class="form-item field text{.if required} required{.end}">
                {@|apply individual-input-label}
                {.section description}<div class="description">{@}</div>{.end}
                <input
                    class="field-element text"
                    type="text"
                    id="{id|htmlattr}-field"
                    {.section placeholder}placeholder="{@|htmlattr}"{.end}
                    {.if required}aria-required="true"{.end}
                />
              </div>
            {.end}

            {.if textarea}
              <div id="{id|htmlattr}"{.if maxLength} data-max-length="{maxLength}"{.end} class="form-item field textarea{.if required} required{.end}">
                {@|apply individual-input-label}
                {.section description}<div class="description">{@}</div>{.end}
                <textarea
                    class="field-element {size}"
                    id="{id|htmlattr}-field"
                    {.section placeholder}placeholder="{@|htmlattr}"{.end}
                    {.if required}aria-required="true"{.end}
                ></textarea>
              </div>
            {.end}

            {.if email}
              <div id="{id|htmlattr}" class="form-item field email{.if required} required{.end}">
                {@|apply individual-input-label}
                {.section description}<div class="description">{@}</div>{.end}
                <input
                    class="field-element"
                    id="{id|htmlattr}-field"
                    name="email"
                    type="email"
                    autocomplete="email"
                    spellcheck="false"
                    {.section placeholder}placeholder="{@|htmlattr}"{.end}
                    {.if required}aria-required="true"{.end}
                />
              </div>
            {.end}

            {.if currency}
              <div id="{id|htmlattr}" class="form-item field currency {.if currencySymbol}hassymbol{.end}{.if required} required{.end}">
                {@|apply individual-input-label}
                {.section description}<div class="description">{@}</div>{.end}
                <input
                    class="field-element"
                    type="text"
                    id="{id|htmlattr}-field"
                    autocomplete="false"
                    spellcheck="false"
                    {.if required}aria-required="true"{.end}
                />
                {.section currencySymbol}<div class="prefix">{@|html}</div>{.end}
              </div>
            {.end}

            {.if number}
              <div id="{id|htmlattr}" class="form-item field number{.if required} required{.end}">
                {@|apply individual-input-label}
                {.section description}<div class="description">{@}</div>{.end}
                <input
                    class="field-element"
                    type="text"
                    id="{id|htmlattr}-field"
                    spellcheck="false"
                    {.section placeholder}placeholder="{@|html}"{.end}
                    {.if required}aria-required="true"{.end}
                />
              </div>
            {.end}

            {.if website}
              <div id="{id|htmlattr}" class="form-item field website{.if required} required{.end}">
                {@|apply individual-input-label}
                {.section description}<div class="description">{@}</div>{.end}
                <input
                    class="field-element"
                    type="text"
                    id="{id|htmlattr}-field"
                    spellcheck="false"
                    {.section placeholder}placeholder="{@|htmlattr}"{.end}
                    {.if required}aria-required="true"{.end}
                />
                <div class="prefix">http://</div>
              </div>
            {.end}

            {.if password}
              <div id="{id|htmlattr}" class="form-item field password{.if required} required{.end}">
                {@|apply individual-input-label}
                {.section description}<div class="description">{@}</div>{.end}
                <input
                    class="field-element"
                    type="password"
                    id="{id|htmlattr}-field"
                    {.if required}aria-required="true"{.end}
                />
              </div>
            {.end}

            {.if twitter}
              <div id="{id|htmlattr}" class="form-item field twitter{.if required} required{.end}">
                {@|apply individual-input-label}
                {.section description}<div class="description">{@}</div>{.end}
                <input
                    class="field-element"
                    type="text"
                    id="{id|htmlattr}-field"
                    spellcheck="false"
                    {.if required}aria-required="true"{.end}
                />
                <div class="prefix">@</div>
              </div>
            {.end}

            {.if radio}
              <fieldset
                  id="{id|htmlattr}"
                  class="form-item field radio{.if required} required{.end}"
                  {.if required}aria-required="true"{.end}
              >
                {@|apply fieldset-legend}
                {.section description}<div class="description">{@}</div>{.end}
                {.repeated section options}
                  <div class="option"><label><input type="radio" name="{id|htmlattr}-field" value="{@|htmlattr}" /> {@|html}</label></div>
                {.end}
              </fieldset>
            {.end}

            {.if checkbox}
              <fieldset
                  id="{id|htmlattr}"
                  class="form-item field checkbox{.if required} required{.end}"
                  {.if required}aria-required="true"{.end}
              >
                {@|apply fieldset-legend}
                {.section description}<div class="description">{@}</div>{.end}
                {.repeated section options}
                <div class="option"><label><input type="checkbox" name="{id|htmlattr}-field" value="{@|htmlattr}"/> {@|html}</label></div>
                {.end}
              </fieldset>
            {.end}

            {.if select}
              <div id="{id|htmlattr}" class="form-item field select{.if required} required{.end}">
                {@|apply individual-input-label}
                {.section description}<div class="description">{@}</div>{.end}
                {##
                  The `name` attribute is probably unused, leaving it here
                  just because it's recommended for HTML form elements
                  to have names.
                ##}
                <select
                    id="{id|htmlattr}-field"
                    name="{id|htmlattr}-field"
                    {.if required}aria-required="true"{.end}
                >
                  {.repeated section options}
                    <option value="{@|htmlattr}">{@|html}</option>
                  {.end}
                  <optgroup label=""></optgroup>
                </select>
              </div>
            {.end}

            {.if name}
              <fieldset id="{id|htmlattr}" class="form-item fields name{.if required} required{.end}">
                {@|apply fieldset-legend}
                {.section description}<div class="description">{@}</div>{.end}
                <div class="field first-name">
                  <label class="caption">
                    <input
                        class="field-element field-control"
                        name="fname"
                        x-autocompletetype="given-name"
                        type="text"
                        spellcheck="false"
                        maxlength="30"
                        data-title="First"
                        {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.name.firstName}</span>
                  </label>
                </div>
                <div class="field last-name">
                  <label class="caption">
                    <input
                        class="field-element field-control"
                        name="lname"
                        x-autocompletetype="surname"
                        type="text"
                        spellcheck="false"
                        maxlength="30"
                        data-title="Last"
                        {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.name.lastName}</span>
                  </label>
                </div>
              </fieldset>
            {.end}

            {.if date}
              <fieldset id="{id|htmlattr}" class="form-item fields date{.if required} required{.end}">
                {@|apply fieldset-legend}
                {.section description}<div class="description">{@}</div>{.end}
                <div class="field month two-digits">
                  <label class="caption">
                    <input
                        class="field-element"
                        type="text"
                        maxlength="2"
                        data-title="Month"
                        {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.date.mm}</span>
                  </label>
                </div>
                <div class="field day two-digits">
                  <label class="caption">
                    <input
                      class="field-element"
                      type="text"
                      maxlength="2"
                      data-title="Day"
                      {.if required}aria-required="true"{.end}
                      />
                    <span class="caption-text">{localizedStrings.date.dd}</span>
                  </label>
                </div>
                <div class="field year four-digits">
                  <label class="caption">
                    <input
                        class="field-element"
                        type="text"
                        maxlength="4"
                        data-title="Year"
                        {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.date.yyyy}</span>
                  </label>
                </div>
              </fieldset>
            {.end}

            {.if time}
              <fieldset id="{id|htmlattr}" class="form-item fields time{.if required} required{.end}">
                {@|apply fieldset-legend}
                {.section description}<div class="description">{@}</div>{.end}
                <div class="field hour two-digits">
                  <label class="caption">
                    <input
                      class="field-element"
                      type="text"
                      maxlength="2"
                      data-title="Hour"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.time.hour}</span>
                  </label>
                </div>
                <div class="field minute two-digits">
                  <label class="caption">
                    <input
                      class="field-element"
                      type="text"
                      maxlength="2"
                      data-title="Minute"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.time.minute}</span>
                  </label>
                </div>
                <div class="field second two-digits">
                  <label class="caption">
                    <input
                      class="field-element"
                      type="text"
                      maxlength="2"
                      data-title="Second"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.time.second}</span>
                  </label>
                </div>
                <div class="field ampm">
                  <select
                    class="field-element"
                    data-title="Ampm"
                    {.if required}aria-required="true"{.end}
                  >
                    <option value="AM">{localizedStrings.time.am}</option>
                    <option value="PM">{localizedStrings.time.pm}</option>
                  </select>
                </div>
              </fieldset>
            {.end}

            {.if address}
              <fieldset id="{id|htmlattr}" class="form-item fields address{.if required} required{.end}">
                {@|apply fieldset-legend}
                {.section description}<div class="description">{@}</div>{.end}
                <div class="field address1">
                  <label class="caption">
                    <input
                      class="field-element field-control"
                      name="address"
                      x-autocompletetype="address-line1"
                      type="text"
                      spellcheck="false"
                      data-title="Line1"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.address.address1}</span>
                  </label>
                </div>
                <div class="field address2">
                  <label class="caption">
                    <input
                      class="field-element field-control"
                      name="address2"
                      x-autocompletetype="address-line2"
                      type="text"
                      spellcheck="false"
                      data-title="Line2"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.address.address2}</span>
                  </label>
                </div>
                <div class="field city">
                  <label class="caption">
                    <input
                      class="field-element field-control"
                      name="city"
                      x-autocompletetype="city"
                      type="text"
                      spellcheck="false"
                      data-title="City"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.address.city}</span>
                  </label>
                </div>
                <div class="field state-province">
                  <label class="caption">
                    <input
                      class="field-element field-control"
                      name="state"
                      x-autocompletetype="state"
                      type="text"
                      spellcheck="false"
                      data-title="State"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.address.stateProvince}</span>
                  </label>
                </div>
                <div class="field zip">
                  <label class="caption">
                    <input
                      class="field-element field-control"
                      name="zipcode"
                      x-autocompletetype="postal-code"
                      type="text"
                      spellcheck="false"
                      data-title="Zip"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.address.zipPostalCode}</span>
                  </label>
                </div>
                <div class="field country">
                  <label class="caption">
                    <input
                      class="field-element field-control"
                      name="country"
                      x-autocompletetype="country"
                      type="text"
                      spellcheck="false"
                      data-title="Country"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">{localizedStrings.address.country}</span>
                  </label>
                </div>
              </fieldset>
            {.end}

            {.if phone}
              <fieldset id="{id|htmlattr}" class="form-item fields phone{.if required} required{.end}">
                {@|apply fieldset-legend}
                {.section description}<div class="description">{@}</div>{.end}
                {.if showCountryCode}
                  <div class="field text two-digits">
                    <label class="caption">
                      <input
                        class="field-element"
                        x-autocompletetype="phone-country-code"
                        type="text"
                        maxlength="5"
                        data-title="Country"
                        {.if required}aria-required="true"{.end}
                      />
                      <span class="caption-text">{localizedStrings.phone.country}</span>
                    </label>
                  </div>
                {.end}
                <div class="field text three-digits">
                  <label class="caption">
                    <input
                      class="field-element"
                      x-autocompletetype="phone-area-code"
                      type="text"
                      maxlength="3"
                      data-title="Areacode"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">(###)</span>
                  </label>
                </div>
                <div class="field text three-digits">
                  <label class="caption">
                    <input
                      class="field-element"
                      x-autocompletetype="phone-local-prefix"
                      type="text"
                      maxlength="3"
                      data-title="Prefix"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">###</span>
                  </label>
                </div>
                <div class="field text four-digits">
                  <label class="caption">
                    <input
                      class="field-element"
                      x-autocompletetype="phone-local-suffix"
                      type="text"
                      maxlength="4"
                      data-title="Line"
                      {.if required}aria-required="true"{.end}
                    />
                    <span class="caption-text">####</span>
                  </label>
                </div>
              </fieldset>
            {.end}

            {.if likert}
              <fieldset id="{id|htmlattr}" class="form-item field likert{.if required} required{.end}">
                {@|apply fieldset-legend}
                {.section description}<div class="description">{@}</div>{.end}
                {.repeated section options}
                  <fieldset
                      data-question="{@|htmlattr}"
                      class="item"
                      {.if required}aria-required="true"{.end}
                  >
                    <legend class="question">{@|html}</legend>
                    <div class="option">
                      <label>
                        <input type="radio" name="{id|htmlattr}-field-{@|htmlattr}" value="-2" /> {localizedStrings.likert.stronglyDisagree}
                      </label>
                    </div>
                    <div class="option">
                      <label>
                        <input type="radio" name="{id|htmlattr}-field-{@|htmlattr}" value="-1" /> {localizedStrings.likert.disagree}
                      </label>
                    </div>
                    <div class="option">
                      <label>
                        <input type="radio" name="{id|htmlattr}-field-{@|htmlattr}" value="0" /> {localizedStrings.likert.neutral}
                      </label>
                    </div>
                    <div class="option">
                      <label>
                        <input type="radio" name="{id|htmlattr}-field-{@|htmlattr}" value="1" /> {localizedStrings.likert.agree}
                      </label>
                    </div>
                    <div class="option">
                      <label>
                        <input type="radio" name="{id|htmlattr}-field-{@|htmlattr}" value="2" /> {localizedStrings.likert.stronglyAgree}
                      </label>
                    </div>
                  </fieldset>
                {.end}
              </fieldset>
            {.end}

        {.end}

        </div>

      {.end}

      {.if captchaEnabled}
      <div class="captcha-container
        {.equal? captchaAlignment 1}align-left{.end}
        {.equal? captchaAlignment 2}align-center{.end}
        {.equal? captchaAlignment 3}align-right{.end}
        "
        data-theme="{.equal? captchaTheme 1}light{.end}{.equal? captchaTheme 2}dark{.end}"
      ></div>
      {.end}

      {.if hideSubmitButton}{.or}
      <div {.if useLightbox}{.or}data-animation-role="button"{.end}
        class="
          form-button-wrapper
          {.if buttonAlignment}
            form-button-wrapper--align-{buttonAlignment}
          {.end}
        "
      >
        <input
          class="button sqs-system-button sqs-editable-button sqs-button-element--primary"
          type="submit"
          value="{.if formSubmitButtonText}{formSubmitButtonText|htmlattr}{.or}{localizedStrings.submit}{.end}"
          data-sqsp-button
        />
      </div>
      {.end}

      {.if disclaimerMessage}
        {.if disclaimerMessage.html}
          {.section disclaimerMessage}<div class="disclaimer-container disclaimerMessage">{html}</div>{.end}
        {.end}
      {.end}
      <div class="hidden form-submission-text">{.section formSubmissionMessage}{html}{.end}</div>
      <div class="hidden form-submission-html" data-submission-html="{formSubmissionHTML|htmlattr}"></div>
    </form>
  </div>
</div>
